mapper.update:
UPDATE session
SET title = #{title},
    last_activity = #{lastActivity},
    status = #{status}
WHERE id = #{id}

mapper.select:
SELECT * FROM session
WHERE user_id = #{userId}
ORDER BY last_activity DESC

